	iResult = m_pNpcList[iNpcH]->m_iMagicHitRatio;

	// ������ ���� ���� ��ݷ� ����
	iWhetherBonus = iGetWhetherMagicBonusEffect(sType, m_pMapList[m_pNpcList[iNpcH]->m_cMapIndex]->m_cWhetherStatus);

	// v1.41 ���� �Ӽ�
