		dwp = (DWORD *) (cData + DEF_INDEX4_MSGID);
		*dwp = MSGID_NOTIFY;
		wp = (WORD *) (cData + DEF_INDEX2_MSGTYPE);
		*wp = DEF_NOTIFY_ITEMOBTAINED;

		cp = (char *) (cData + DEF_INDEX2_MSGTYPE + 2);

		// 1�� ȹ���ߴ�. Amount�� �ƴϴ�!
		*cp = 1;
		cp++;

		memcpy(cp, pItem->m_cName, 20);
		cp += 20;

		dwp = (DWORD *) cp;
